INSERT INTO `hw_expert` VALUES ('1', '专家页头部照片', '', 'Upload/2016-10-07/57f6f2546331c.jpg', '');
INSERT INTO `hw_expert` VALUES ('51', '豆立宁', '国家注册心理咨询师&amp;国家注册沙盘游戏师；中国医科大学应用心理学硕士，盛京医院心理科近4年的科研与临床实习，熟悉各种儿童青少年心理问题及其他情绪情感障碍的评估；擅长领域：儿童青少年心理健康，家庭教育，成人情绪情感障碍；技术取向：精神（心理）分析，沙盘游戏疗法，动力性家庭(团体)治疗\r\n', 'Upload/2016-10-06/57f5af44348a0.jpg', '&lt;p&gt;国家注册二级心理咨询师，国家注册沙盘游戏师，沈阳瀚威心理咨询有限公司总经理，瀚威心理咨询师建设项目负责人。&lt;/p&gt;');
INSERT INTO `hw_expert` VALUES ('52', '于爽', '国家注册心理咨询师；近三年的个案咨询经验，累计咨询时数约400小时。专攻各种儿童青少年心理问题；参加专业培训经历：东方心理分析研究院沙盘游戏师初、中、高级培训，曾奇峰精神分析培训；擅长领域：儿童青少年心理健康；技术取向：沙盘游戏疗法，以人为中心心理咨询疗法，精神（心理）分析。\r\n', 'Upload/2016-10-06/57f5b0616bf28.jpg', '&lt;p&gt;国家注册二级心理咨询师，国家注册沙盘游戏师，沈阳瀚威心理咨询有限公司总经理，瀚威心理咨询师建设项目负责人。&lt;/p&gt;');
INSERT INTO `hw_expert` VALUES ('53', '王焕', '国家注册心理咨询师&amp;国家注册意象对话心理师；培训经历：中国心理研究所 儿童发展与教育心理学专业课程，儿童心理游戏培训，国际沙盘游戏治疗师认证培训，OH卡潜意识探索心理咨询方法，0-6岁儿童心理预防与发展培训课程；擅长领域：儿童心理健康咨询；技术取向：精神（心理）分析，沙盘游戏疗法。\r\n', 'Upload/2016-10-06/57f5b072208b9.jpg', '&lt;p&gt;国家注册二级心理咨询师，国家注册沙盘游戏师，沈阳瀚威心理咨询有限公司总经理，瀚威心理咨询师建设项目负责人。&lt;/p&gt;');
INSERT INTO `hw_expert` VALUES ('54', '宋晓东', '国家注册心理咨询师；经过心理资格培训取得国家心理咨询师证书，千六百时咨询经验。参加专业培训经历：分析心理学课程（52课时）；擅长领域：成人情绪情感障碍；技术取向：分析心理，焦点技术，oh卡潜意识疗法，催眠放松疗法，灵性心理成长。', 'Upload/2016-10-06/57f5b0ab244b9.jpg', null);
INSERT INTO `hw_expert` VALUES ('57', '张怡', '国家注册心理咨询师&amp;国家注册沙盘游戏师&amp;国家注册意象对话心理师；培训经历：国际沙盘游戏治疗师认证培训，长程动力性家庭治疗连续培训，朱建军意象对话，苏格兰灵性关怀课程；擅长领域：儿童青少年心理健康，家庭教育，成人情绪情感障碍；技术取向：精神（心理）分析，沙盘游戏疗法，认知行为疗法，意象对话。\r\n', 'Upload/2016-10-06/57f5b0854af61.jpg', null);
INSERT INTO `hw_expert` VALUES ('58', '高建慧', '国家二级心理咨询师；专业培训经历：长期接受精神分析的理论及实践培训，参加并学习雷正则老师的精神分析理论与临床实务班三阶，孟德军老师的精神分析临床录音观摩班、防御机制系列课程及自体心理学系列课程，萨提亚家庭心理治疗等；擅长领域：个体精神分析方向的动力性心理治疗，个体、团体沙盘心理治疗；技术倾向:精神分析疗法，沙盘游戏治疗。', 'Upload/2016-10-06/57f5b09ccdc05.jpg', null);

