INSERT INTO `hw_machine` VALUES ('1', 'atransform', '头部图片', '&lt;h4 class=&quot;media-heading&quot; style=&quot;box-sizing: border-box; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Microsoft Yahei&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 0px; margin-bottom: 5px; font-size: 18px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Media heading&lt;/h4&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(51, 51, 51); font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, &amp;quot;Microsoft Yahei&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin commodo. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Donec lacinia congue felis in faucibus.&lt;/span&gt;&lt;/p&gt;', 'Upload/2016-10-07/57f71b7b5d6ad.jpg', 'Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin commodo. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Donec lacinia congue felis in faucibus.');
INSERT INTO `hw_machine` VALUES ('2', 'Brainwave Ark', '脑波方舟', '&lt;p style=&quot;text-indent: 27px; line-height: 2em;&quot;&gt;&lt;span style=&quot;color: rgb(15, 36, 62); font-size: 16px;&quot;&gt;&lt;span style=&quot;color: rgb(15, 36, 62); line-height: 150%; font-family: 微软雅黑;&quot;&gt;运用脑电波读取，转化为电信号的工作原理，可以与朋友进行对抗赛。训练时，需要佩戴脑波耳机，此耳机采用脑电传感技术，通过置于头部的传感器检测出您的脑波，经过信号的分析及解读完成对小球的控制。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height: 2em;&quot;&gt;&lt;span style=&quot;font-size: 16px; color: rgb(15, 36, 62);&quot;&gt;&lt;span style=&quot;font-size: 16px; color: rgb(15, 36, 62); font-family: 微软雅黑, &amp;#39;Microsoft YaHei&amp;#39;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;', 'Upload/2016-10-07/57f71d86c4688.jpg', '运用脑电波读取，转化为电信号的工作原理，可以与朋友进行对抗赛。训练时，需要佩戴脑波耳机，此耳机采用脑电传感技术，通过置于头部的传感器检测出您的脑波，经过信号的分析及解读完成对小球的控制。\r\n');
INSERT INTO `hw_machine` VALUES ('3', 'Body &amp; Mind Feedback System', '身心反馈系统', '&lt;p style=&quot;line-height: 2em; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; line-height: 150%; font-size: 16px;&quot;&gt;以放松反馈训练为主，通过系统播放专业放松反馈音乐、结合生理传感器采集使用者的生理指标，实时判断使用者的情绪稳定性、自我生理协调状态，根据系统制定的反馈训练方案，随时分配不同的音乐治疗处方，在监测生理指标的同时给予使用者减压、放松、催眠暗引导。是集音乐放松反馈、生理指标采集分析为一体的反馈型放松产品。&lt;/span&gt;&lt;/p&gt;', 'Upload/2016-10-07/57f71d09c2f7b.jpg', '以放松反馈训练为主，通过系统播放专业放松反馈音乐、结合生理传感器采集使用者的生理指标，实时判断使用者的情绪稳定性、自我生理协调状态，根据系统制定的反馈训练方案，随时分配不同的音乐治疗处方，在监测生理指标的同时给予使用者减压、放松、催眠暗引导。是集音乐放松反馈、生理指标采集分析为一体的反馈型放松产品。');
INSERT INTO `hw_machine` VALUES ('4', 'Mind Racing', '意念赛车', '&lt;p style=&quot;line-height: 2em; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 16px;&quot;&gt;脑波赛车运用脑电生物反馈原理，使用者的专注力越强，赛车速度越快。科学实验已经证明脑电生物反馈训练系统可以有效提高使用者集中注意力的能力，减少慢波的活动，并延长需要解决问题时集中注意力的时间。&lt;/span&gt;&lt;/p&gt;', 'Upload/2016-10-07/57f7305b27c95.jpg', '脑波赛车运用脑电生物反馈原理，使用者的专注力越强，赛车速度越快。科学实验已经证明脑电生物反馈训练系统可以有效提高使用者集中注意力的能力，减少慢波的活动，并延长需要解决问题时集中注意力的时间。');

